            gcmONERROR(gcoHARDWAREVX_SetTempReg(0, 5, gcdVX_SWIZZLE1(0), 0, &Instructions->binarys[Instructions->count]));
            gcmONERROR(gcoHARDWAREVX_SetTempReg(1, 5, gcdVX_SWIZZLE1(2), 0, &Instructions->binarys[Instructions->count]));
