        gcmONERROR(gcoHARDWAREVX_SetDestination(2, gcdVX_ENABLE, gcvFALSE, &Instructions->binarys[Instructions->count]));
        gcmONERROR(gcoHARDWAREVX_SetEVIS(0, 3, (0x7 | (0x7 << 3)), &Instructions->binarys[Instructions->count]));
