    gcmONERROR(gcoHARDWAREVX_SetEVIS(0, 3, (Input | (Input << 3)), &Instructions->binarys[Instructions->count]));
    gcmONERROR(gcoHARDWAREVX_SetTempReg(0, 1, gcdVX_SWIZZLE, 0, &Instructions->binarys[Instructions->count]));
