            gcmONERROR(gcoHARDWAREVX_AddOpcode(0x09, 0x02, 0x2, &Instructions->binarys[Instructions->count]));
            gcmONERROR(gcoHARDWAREVX_SetDestination(4, gcdVX_ENABLE1(1), gcvFALSE, &Instructions->binarys[Instructions->count]));
