            gcmONERROR(gcoHARDWAREVX_SetTempReg(0, 9, gcdVX_SWIZZLE1(0), 0, &Instructions->binarys[Instructions->count]));
            gcmONERROR(gcoHARDWAREVX_SetTempReg(1, 9, gcdVX_SWIZZLE1(1), 0, &Instructions->binarys[Instructions->count]));
